    private void jButton16ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton16ActionPerformed

        String supplier = suppbox2.getText();
        Integer qty = Integer.valueOf(jTextField3.getText());
        String date = datetxt2.getText();
        String item = namebox2.getText();
        String category = catebox2.getSelectedItem().toString();
        String UOM = uombox2.getSelectedItem().toString();
        String description=descriBox1.getText();

        try{

            String sql = "insert into requested(	supName,qty,date_of_order,itemName,category,UOM,description)values('"+supplier+"','"+qty+"','"+date+"','"+item+"','"+category+"','"+UOM+"','"+description+"')";
            pst = conn.prepareStatement(sql);
            pst.execute();
            // System.out.println("data added");
            JOptionPane.showMessageDialog(null, "Data Successfullt Added!!!");

        }catch(Exception e){

            JOptionPane.showMessageDialog(null, "Error"+e);

        }

    }//GEN-LAST:event_jButton16ActionPerformed

